import React, { Component } from 'react';
import BoozeList from './BoozeList';
import BoozeSpecs from './BoozeSpecs';
import SearchForm from './SearchForm';
import AddForm from './AddForm';
import { Route } from 'react-router-dom';

export default class CocktailsContainer extends Component {
	state = { cocktails: [], favCocktails: [] };

	componentDidMount() {
		fetch('http://localhost:3000/api/v1/cocktails')
			.then(res => res.json())
			.then(cocktails => this.setState({ cocktails }));
	}

	goBack = () => {
		this.setState({ targetCocktailId: 0 });
	};

	toggleFav = cocktailId => {
		if (this.state.favCocktails.includes(cocktailId)) {
			let newFavCocktails = [...this.state.favCocktails];
			let index = newFavCocktails.findIndex(id => id === cocktailId);
			newFavCocktails.splice(index, 1);
			this.setState({ favCocktails: newFavCocktails });
			console.log('unfavorited');
		} else {
			this.setState({ favCocktails: [...this.state.favCocktails, cocktailId] });
			console.log('added to favorites');
		}
	};

	render() {
		return (
			<div className="ui grid container">
				<div className="row">
					<div className="three wide column">
						<SearchForm />
						<br />
						<hr />
						<br />
						<AddForm />
					</div>
					<div className="thirteen wide column">
						<Route
							exact
							path="/"
							render={() => (
								<BoozeList
									cocktails={this.state.cocktails}
									handleClick={this.setTargetCocktail}
								/>
							)}
						/>
						<Route
							exact
							path="/booze/:id"
							render={renderProps => (
								<BoozeSpecs
									{...renderProps}
									goBack={this.goBack}
									toggleFav={this.toggleFav}
								/>
							)}
						/>
					</div>
				</div>
			</div>
		);
	}
}
