import React, { Component } from 'react';
import CocktailsList from './CocktailsList';
import CocktailCard from './CocktailCard';
import CocktailSpecs from './CocktailSpecs';
import { Switch, Route, Redirect } from 'react-router-dom';

class CocktailsContainer extends Component {
	state = {
    cocktails: []
  }

	componentDidMount() {
		fetch('http://localhost:3000/api/v1/cocktails')
			.then(res => res.json())
			.then(cocktails => this.setState({ cocktails }));
	}


	render() {
		return (
			<div className="ui grid container">
				<div className="row">
					<div className="three wide column">

					</div>
					<div className="thirteen wide column">

					<CocktailsList
						cocktails={this.state.cocktails}
						selectCocktail={this.selectCocktail}
					/>


					<Route
						exact
						path="/cocktail/:id"
						render={renderProps => (
							<CocktailSpecs
								{...renderProps}

							/>
						)}
					/>

					</div>
				</div>
			</div>
		);
	}
}

render = () => {
	return (
		<div className="ui grid container">
			<DogList
				width="six"
				dogs={this.state.dogs}
				selectDog={this.selectDog}
				toggleForm={this.showForm}
			/>
			<Switch>
				<Route path="/dogs/new" render={() => (
					localStorage.getItem("name") ? (
						<DogForm addDog={this.addDog} />
					) : <Redirect to="/login" />
				)} />
				<Route path="/dogs/:id" render={({ match }) => {
					let dogId = parseInt(match.params.id)
					let dog = this.state.dogs.find(dog => dog.id === dogId)
					return dog ? <DogDetail width="ten" dog={dog} /> : null;
				}} />
			</Switch>
		</div>
	)
}

export default CocktailsContainer
