import React from 'react';
import { Link } from 'react-router-dom';

class CocktailSpect extends Component {

  state = {
    id: 0,
    name: '',
    description: '',
    instructions: '',
    proportions: [],
    fav: false,
    seeComments: false
    // comments: [],
  };

  componentDidMount() {
    fetch(
      `http://localhost:3000/api/v1/cocktails/${this.props.match.params.id}`
    )
      .then(res => res.json())
      .then(cocktail =>
        this.setState({
          id: cocktail.id,
          name: cocktail.name,
          description: cocktail.description,
          instructions: cocktail.instructions,
          proportions: [...cocktail.proportions]
        })
      );
  }

  generateProportions = () =>
    this.state.proportions.map(p => ` ${p.amount} of ${p.ingredient_name}`);

  handleSeeComments = () => {
    this.setState({ seeComments: !this.state.seeComments });
  };

  generateCommentcards = () => {
    return this.state.seeComments ? <CommentCard /> : null;
  };

  commentForm = () => {
    return this.seeComments ? (
      <form>
        <input type="text" />
        <input type="submit" />
      </form>
    ) : null;
  };

  favIconClass = () =>
    this.state.fav
      ? 'right floated like icon active'
      : 'right floated like icon';

  toggleFav = () => {
    this.props.toggleFav(this.state.id);
    this.setState({ fav: !this.state.fav });
  };

  render() {
    console.log(this.props);
    return (
      <div className="ui fluid card">
        <div className="content">
          <i className={this.favIconClass()} onClick={this.toggleFav} />
          <div className="header">{this.state.name}</div>
          <div className="meta">{this.state.source}</div>
          <div className="description">
            <i className="triangle right icon" />
            {this.state.description}
          </div>
          <div className="description">
            <i className="triangle right icon" />
            To make: {this.state.instructions}
          </div>
          <div className="description">
            <i className="triangle right icon" />
            Includes{this.generateProportions()}
          </div>
        </div>
        <div className="content extra">
          <Link to="/">
            <button className="ui button">Go Back</button>
          </Link>
          <button
            className="ui button right floated"
            onClick={this.handleSeeComments}>
            See Comments
          </button>
        </div>
        <div className="content extra">{this.generateCommentcards()}</div>
      </div>
    );
  }
}
export default CocktailSpecs
